mapper.select:
SELECT tcc.id, tcc.equipment_name equipmentName, CONCAT(tcr.teach_building, '-', tcr.floor, '-', tcr.room_num) locationName, ogc.xq, ogc.nj, ogc.xd FROM teach_class_card tcc LEFT JOIN org_grade_class ogc ON tcc.class_id = ogc.id LEFT JOIN teach_class_room tcr ON tcc.teach_class_room_id = tcr.id WHERE tcc.school_id = #{schoolId,jdbcType=INTEGER} AND tcc.school_id = tcr.school_id AND tcc.del_flag = 0 AND tcr.del_flag = 0
and tcc.class_id in
#{classid}
and ogc.xd = #{xd}
and ogc.xq = #{xq,jdbcType=INTEGER}
and ogc.nj = #{nj,jdbcType=INTEGER}
order by tcc.update_date desc

SELECT tcc.id, tcc.equipment_name equipmentName, ocs.name xd, ogc.nj FROM teach_class_card tcc LEFT JOIN org_grade_class ogc ON tcc.class_id = ogc.id LEFT JOIN org_class_section ocs ON ocs.id = ogc.xd WHERE tcc.del_flag = 0 AND tcc.school_id=#{schoolId}
AND ogc.id in
#{classid}
order by concat(ogc.nj,ogc.name,equipment_name)
